import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import StoreCard from '../components/StoreCard';
import { FaSearch, FaStar, FaStore } from 'react-icons/fa';

const Home = () => {
  const [topStores, setTopStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTopStores = async () => {
      try {
        const response = await axios.get('/api/stores', {
          params: {
            limit: 4,
            sort: 'averageRating',
            order: 'DESC'
          }
        });
        
        // Check if response.data.stores exists, otherwise use empty array or direct data
        if (response.data && Array.isArray(response.data.stores)) {
          setTopStores(response.data.stores);
        } else if (response.data && Array.isArray(response.data)) {
          // If data is returned directly as an array
          setTopStores(response.data);
        } else {
          // Fallback to empty array if no valid data
          console.warn('No valid stores data returned from API');
          setTopStores([]);
        }
      } catch (err) {
        console.error('Error fetching top stores:', err);
        setError('Failed to load top stores. Please try again later.');
        setTopStores([]); // Set to empty array on error
      } finally {
        setLoading(false);
      }
    };

    fetchTopStores();
  }, []);

  return (
    <div className="home-page">
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1 className="hero-title">Find and Rate the Best Stores</h1>
          <p className="hero-subtitle">
            Discover top-rated stores in your area and share your experiences
          </p>
          
          <div className="hero-search">
            <div className="search-box">
              <input
                type="text"
                placeholder="Search for stores..."
                className="search-input"
              />
              <Link to="/dashboard/stores" className="search-btn">
                <FaSearch />
              </Link>
            </div>
            
            <div className="hero-buttons">
              <Link to="/dashboard/stores" className="btn btn-primary">
                Browse Stores
              </Link>
              <Link to="/register" className="btn btn-outline">
                Join Our Community
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="features">
        <div className="container">
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon">
                <FaStore />
              </div>
              <h3 className="feature-title">Discover Stores</h3>
              <p className="feature-description">
                Find the best stores in your area with detailed information and reviews
              </p>
            </div>
            
            <div className="feature-card">
              <div className="feature-icon">
                <FaStar />
              </div>
              <h3 className="feature-title">Rate & Review</h3>
              <p className="feature-description">
                Share your experiences by rating and reviewing stores you've visited
              </p>
            </div>
            
            <div className="feature-card">
              <div className="feature-icon">
                <FaSearch />
              </div>
              <h3 className="feature-title">Easy Search</h3>
              <p className="feature-description">
                Quickly find stores by name, location, or category with our powerful search
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* Top Stores Section */}
      <section className="top-stores">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Top Rated Stores</h2>
            <Link to="/dashboard/stores" className="section-link">
              View All
            </Link>
          </div>
          
          {loading ? (
            <div className="loading">Loading top stores...</div>
          ) : error ? (
            <div className="error">{error}</div>
          ) : (
            <div className="stores-grid">
              {Array.isArray(topStores) && topStores.length > 0 ? (
                topStores.map((store) => (
                  <div className="store-grid-item" key={store.id}>
                    <StoreCard store={store} />
                  </div>
                ))
              ) : (
                <div className="no-stores">No stores found. Check back soon!</div>
              )}
            </div>
          )}
        </div>
      </section>
      
      <style jsx="true">{`
        .hero {
          background: linear-gradient(to right, #4a90e2, #50c878);
          color: white;
          padding: 4rem 0;
          text-align: center;
        }
        
        .hero-content {
          max-width: 800px;
          margin: 0 auto;
          padding: 0 1rem;
        }
        
        .hero-title {
          font-size: 2.5rem;
          margin-bottom: 1rem;
        }
        
        .hero-subtitle {
          font-size: 1.2rem;
          margin-bottom: 2rem;
          opacity: 0.9;
        }
        
        .hero-search {
          display: flex;
          flex-direction: column;
          align-items: center;
          gap: 1rem;
        }
        
        .search-box {
          display: flex;
          width: 100%;
          max-width: 500px;
          background-color: white;
          border-radius: 50px;
          overflow: hidden;
          box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
        }
        
        .search-input {
          flex: 1;
          padding: 1rem 1.5rem;
          border: none;
          font-size: 1rem;
        }
        
        .search-input:focus {
          outline: none;
        }
        
        .search-btn {
          background-color: var(--primary-color);
          color: white;
          padding: 1rem;
          border: none;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
          min-width: 50px;
        }
        
        .hero-buttons {
          display: flex;
          gap: 1rem;
          margin-top: 1rem;
        }
        
        .features {
          padding: 5rem 0;
          background-color: white;
        }
        
        .features-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
          gap: 2rem;
        }
        
        .feature-card {
          text-align: center;
          padding: 2rem;
          border-radius: var(--border-radius);
          transition: var(--transition);
        }
        
        .feature-card:hover {
          transform: translateY(-10px);
        }
        
        .feature-icon {
          font-size: 2.5rem;
          color: var(--primary-color);
          margin-bottom: 1.5rem;
        }
        
        .feature-title {
          font-size: 1.3rem;
          margin-bottom: 1rem;
        }
        
        .feature-description {
          color: var(--light-text);
        }
        
        .top-stores {
          padding: 5rem 0;
          background-color: var(--lightest-gray);
        }
        
        .section-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 2rem;
        }
        
        .section-title {
          font-size: 1.8rem;
          color: var(--text-color);
        }
        
        .section-link {
          color: var(--primary-color);
          font-weight: 500;
        }
        
        .stores-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
          gap: 1.5rem;
        }
        
        .loading, .error {
          text-align: center;
          padding: 2rem;
          background-color: white;
          border-radius: var(--border-radius);
        }
        
        .error {
          color: var(--error-color);
        }
        
        .no-stores {
          text-align: center;
          padding: 2rem;
          background-color: white;
          border-radius: var(--border-radius);
          box-shadow: var(--shadow);
        }
        
        @media (max-width: 768px) {
          .hero-title {
            font-size: 2rem;
          }
          
          .hero-subtitle {
            font-size: 1rem;
          }
          
          .feature-card {
            padding: 1.5rem;
          }
        }
      `}</style>
    </div>
  );
};

export default Home;
