import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaStar, FaSearch, FaMapMarkerAlt, FaPhoneAlt, FaEnvelope } from 'react-icons/fa';
import { useNavigate, useLocation } from 'react-router-dom';

const StoreList = () => {
  const [stores, setStores] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();
  const isDashboard = location.pathname === '/dashboard';

  useEffect(() => {
    fetchStores();
  }, []);

  const fetchStores = async () => {
    try {
      const response = await axios.get('/api/stores');
      setStores(Array.isArray(response.data) ? response.data : []);
    } catch (err) {
      setError('Error fetching stores');
      setStores([]);
    } finally {
      setLoading(false);
    }
  };

  const filteredStores = Array.isArray(stores) 
    ? stores.filter(store =>
        store.name.toLowerCase().includes(searchTerm.toLowerCase())
      )
    : [];

  const renderStoreCards = (stores) => (
    <div className="row">
      {stores.map(store => (
        <div key={store.id} className="col-md-4 mb-4">
          <div className="card h-100 shadow-sm hover-effect">
            {store.imageUrl && (
              <img
                src={store.imageUrl}
                alt={store.name}
                className="card-img-top"
                style={{ height: '200px', objectFit: 'cover' }}
              />
            )}
            <div className="card-body">
              <h5 className="card-title">{store.name}</h5>
              <div className="mb-3">
                <div className="d-flex align-items-center mb-2">
                  <FaMapMarkerAlt className="text-secondary me-2" />
                  <small>{store.address}</small>
                </div>
                {store.phone && (
                  <div className="d-flex align-items-center mb-2">
                    <FaPhoneAlt className="text-secondary me-2" />
                    <small>{store.phone}</small>
                  </div>
                )}
                {store.email && (
                  <div className="d-flex align-items-center mb-2">
                    <FaEnvelope className="text-secondary me-2" />
                    <small>{store.email}</small>
                  </div>
                )}
              </div>
              <div className="mb-3">
                {[...Array(5)].map((_, index) => (
                  <FaStar
                    key={index}
                    className={
                      index < Math.floor(store.averageRating || 0)
                        ? 'text-warning'
                        : 'text-muted'
                    }
                  />
                ))}
                <span className="ms-2">
                  ({(store.averageRating || 0).toFixed(1)})
                  <small className="text-muted ms-1">
                    {store.totalRatings || 0} reviews
                  </small>
                </span>
              </div>
              <button
                className="btn btn-primary w-100"
                onClick={() => navigate(`/stores/${store.id}`)}
              >
                {isDashboard ? 'Rate Store' : 'View Details & Rate'}
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );

  if (loading) {
    return (
      <div className="text-center mt-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      {!isDashboard && (
        <div className="row mb-4">
          <div className="col-md-6 mx-auto">
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                placeholder="Search stores by name..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <span className="input-group-text">
                <FaSearch />
              </span>
            </div>
          </div>
        </div>
      )}

      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      {isDashboard ? (
        <div>
          <h4 className="mb-4">Popular Stores</h4>
          {renderStoreCards(stores.slice(0, 6))} {/* Show only top 6 stores in dashboard */}
        </div>
      ) : (
        <div>
          <h4 className="mb-4">All Stores</h4>
          {filteredStores.length > 0 ? (
            renderStoreCards(filteredStores)
          ) : (
            <div className="col-12 text-center mt-4">
              <p className="text-muted">No stores found matching your criteria</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default StoreList;
