import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { FaUser, FaSignOutAlt, FaBars, FaTimes } from 'react-icons/fa';

const Navbar = () => {
  const { isAuthenticated, user, logout } = useAuth();
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <nav className="navbar">
      <div className="container navbar-container">
        <Link to="/" className="navbar-logo">
          RateMyStore
        </Link>

        <div className="navbar-toggle" onClick={toggleMenu}>
          {menuOpen ? <FaTimes /> : <FaBars />}
        </div>

        <ul className={`navbar-menu ${menuOpen ? 'active' : ''}`}>
          <li className="navbar-item">
            <Link to="/" className="navbar-link" onClick={() => setMenuOpen(false)}>
              Home
            </Link>
          </li>
          
          {isAuthenticated ? (
            <>
              <li className="navbar-item">
                <Link
                  to="/dashboard/stores"
                  className="navbar-link"
                  onClick={() => setMenuOpen(false)}
                >
                  Stores
                </Link>
              </li>
              
              <li className="navbar-item dropdown">
                <span className="navbar-link dropdown-toggle">
                  <FaUser className="navbar-icon" />
                  {user?.name}
                </span>
                <ul className="dropdown-menu">
                  <li>
                    <Link
                      to="/dashboard"
                      className="dropdown-item"
                      onClick={() => setMenuOpen(false)}
                    >
                      Dashboard
                    </Link>
                  </li>
                  <li>
                    <Link
                      to="/dashboard/profile"
                      className="dropdown-item"
                      onClick={() => setMenuOpen(false)}
                    >
                      Profile
                    </Link>
                  </li>
                  {user?.role === 'admin' && (
                    <>
                      <li>
                        <Link
                          to="/dashboard/admin/users"
                          className="dropdown-item"
                          onClick={() => setMenuOpen(false)}
                        >
                          Manage Users
                        </Link>
                      </li>
                      <li>
                        <Link
                          to="/dashboard/admin/stores"
                          className="dropdown-item"
                          onClick={() => setMenuOpen(false)}
                        >
                          Manage Stores
                        </Link>
                      </li>
                    </>
                  )}
                  {user?.role === 'store_owner' && (
                    <li>
                      <Link
                        to="/dashboard/store-owner"
                        className="dropdown-item"
                        onClick={() => setMenuOpen(false)}
                      >
                        My Store
                      </Link>
                    </li>
                  )}
                  <li>
                    <button
                      className="dropdown-item logout-button"
                      onClick={handleLogout}
                    >
                      <FaSignOutAlt className="navbar-icon" />
                      Logout
                    </button>
                  </li>
                </ul>
              </li>
            </>
          ) : (
            <>
              <li className="navbar-item">
                <Link
                  to="/login"
                  className="navbar-link"
                  onClick={() => setMenuOpen(false)}
                >
                  Login
                </Link>
              </li>
              <li className="navbar-item">
                <Link
                  to="/register"
                  className="navbar-link btn btn-primary"
                  onClick={() => setMenuOpen(false)}
                >
                  Register
                </Link>
              </li>
            </>
          )}
        </ul>
      </div>
      
      <style jsx="true">{`
        .navbar {
          background-color: white;
          box-shadow: var(--shadow);
          height: 60px;
          position: sticky;
          top: 0;
          z-index: 100;
        }
        
        .navbar-container {
          display: flex;
          justify-content: space-between;
          align-items: center;
          height: 100%;
        }
        
        .navbar-logo {
          font-size: 1.5rem;
          font-weight: 700;
          color: var(--primary-color);
        }
        
        .navbar-toggle {
          display: none;
          cursor: pointer;
          font-size: 1.5rem;
        }
        
        .navbar-menu {
          display: flex;
          align-items: center;
          list-style: none;
        }
        
        .navbar-item {
          margin-left: 1.5rem;
          position: relative;
        }
        
        .navbar-link {
          color: var(--text-color);
          font-weight: 500;
          display: flex;
          align-items: center;
        }
        
        .navbar-link:hover {
          color: var(--primary-color);
          text-decoration: none;
        }
        
        .navbar-icon {
          margin-right: 0.5rem;
        }
        
        .dropdown-toggle {
          cursor: pointer;
        }
        
        .dropdown:hover .dropdown-menu {
          display: block;
        }
        
        .dropdown-menu {
          display: none;
          position: absolute;
          top: 100%;
          right: 0;
          background-color: white;
          border-radius: var(--border-radius);
          box-shadow: var(--shadow);
          min-width: 180px;
          padding: 0.5rem 0;
          z-index: 110;
        }
        
        .dropdown-item {
          display: flex;
          align-items: center;
          padding: 0.5rem 1rem;
          color: var(--text-color);
          cursor: pointer;
          transition: var(--transition);
          text-decoration: none;
        }
        
        .dropdown-item:hover {
          background-color: var(--light-gray);
          color: var(--primary-color);
        }
        
        .logout-button {
          background: none;
          border: none;
          width: 100%;
          text-align: left;
          font-size: 1rem;
        }
        
        @media (max-width: 768px) {
          .navbar-toggle {
            display: block;
          }
          
          .navbar-menu {
            display: flex;
            flex-direction: column;
            position: absolute;
            top: 60px;
            left: 0;
            right: 0;
            background-color: white;
            box-shadow: var(--shadow);
            padding: 1rem;
            transition: transform 0.3s ease-in-out;
            transform: translateY(-100%);
            opacity: 0;
            visibility: hidden;
          }
          
          .navbar-menu.active {
            transform: translateY(0);
            opacity: 1;
            visibility: visible;
          }
          
          .navbar-item {
            margin: 0.5rem 0;
            width: 100%;
          }
          
          .dropdown-menu {
            position: static;
            box-shadow: none;
            padding-left: 1rem;
            display: block;
          }
        }
      `}</style>
    </nav>
  );
};

export default Navbar;
