import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaStar } from 'react-icons/fa';

const StoreOwnerDashboard = () => {
  const [storeData, setStoreData] = useState({
    averageRating: 0,
    ratings: []
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchStoreData();
  }, []);

  const fetchStoreData = async () => {
    try {
      const response = await axios.get('/api/stores/owner/ratings');
      setStoreData(response.data);
    } catch (err) {
      navigate('/login');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="container mt-4">
      {/* Simple Header with Logout */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>My Store</h2>
        <div>
          <Link to="/change-password" className="btn btn-primary me-2">
            Change Password
          </Link>
          <button onClick={handleLogout} className="btn btn-danger">
            Logout
          </button>
        </div>
      </div>

      {/* Average Rating Display */}
      <div className="card mb-4">
        <div className="card-body text-center">
          <h3>Store Rating</h3>
          <div className="display-4">
            {storeData.averageRating.toFixed(1)}
            <FaStar className="ms-2 text-warning" />
          </div>
        </div>
      </div>

      {/* Ratings List */}
      <div className="card">
        <div className="card-header">
          <h5 className="mb-0">Customer Ratings</h5>
        </div>
        <div className="card-body">
          <div className="table-responsive">
            <table className="table">
              <thead>
                <tr>
                  <th>Customer</th>
                  <th>Rating</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {storeData.ratings.length > 0 ? (
                  storeData.ratings.map(rating => (
                    <tr key={rating.id}>
                      <td>{rating.username}</td>
                      <td>
                        {[...Array(5)].map((_, i) => (
                          <FaStar
                            key={i}
                            className={
                              i < rating.rating ? 'text-warning' : 'text-muted'
                            }
                          />
                        ))}
                      </td>
                      <td>
                        {new Date(rating.date).toLocaleDateString()}
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="3" className="text-center">
                      No ratings yet
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StoreOwnerDashboard;
