import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { useAuth } from '../context/AuthContext';

// Validation schema
const schema = yup.object().shape({
  name: yup
    .string()
    .required('Name is required')
    .min(2, 'Name must be at least 2 characters')
    .max(60, 'Name must not exceed 60 characters'),
  email: yup
    .string()
    .email('Please enter a valid email')
    .required('Email is required'),
  password: yup
    .string()
    .required('Password is required')
    .min(8, 'Password must be at least 8 characters')
    .max(16, 'Password must not exceed 16 characters')
    .matches(/[A-Z]/, 'Password must contain at least one uppercase letter')
    .matches(
      /[!@#$%^&*(),.?":{}|<>]/,
      'Password must contain at least one special character'
    ),
  confirmPassword: yup
    .string()
    .oneOf([yup.ref('password'), null], 'Passwords must match')
    .required('Confirm password is required'),
  address: yup
    .string()
    .max(400, 'Address must not exceed 400 characters')
});

const Register = () => {
  const { register: registerUser } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  
  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm({
    resolver: yupResolver(schema)
  });
  
  const onSubmit = async (data) => {
    setLoading(true);
    setError('');
    
    try {
      // Remove confirmPassword as it's not needed for API call
      const { confirmPassword, ...userData } = data;
      
      const result = await registerUser(userData);
      
      if (result.success) {
        navigate('/dashboard');
      } else {
        setError(result.message);
      }
    } catch (err) {
      setError('An unexpected error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2 className="auth-title">Create an Account</h2>
        <p className="auth-subtitle">Join our community and start rating stores!</p>
        
        {error && <div className="auth-error">{error}</div>}
        
        <form className="auth-form" onSubmit={handleSubmit(onSubmit)}>
          <div className="form-group">
            <label htmlFor="name" className="form-label">
              Full Name
            </label>
            <input
              type="text"
              id="name"
              className={`form-control ${errors.name ? 'is-invalid' : ''}`}
              {...register('name')}
              placeholder="Enter your full name"
            />
            {errors.name && (
              <div className="form-error">{errors.name.message}</div>
            )}
          </div>
          
          <div className="form-group">
            <label htmlFor="email" className="form-label">
              Email Address
            </label>
            <input
              type="email"
              id="email"
              className={`form-control ${errors.email ? 'is-invalid' : ''}`}
              {...register('email')}
              placeholder="Enter your email"
            />
            {errors.email && (
              <div className="form-error">{errors.email.message}</div>
            )}
          </div>
          
          <div className="form-group">
            <label htmlFor="password" className="form-label">
              Password
            </label>
            <input
              type="password"
              id="password"
              className={`form-control ${errors.password ? 'is-invalid' : ''}`}
              {...register('password')}
              placeholder="Enter your password"
            />
            {errors.password && (
              <div className="form-error">{errors.password.message}</div>
            )}
          </div>
          
          <div className="form-group">
            <label htmlFor="confirmPassword" className="form-label">
              Confirm Password
            </label>
            <input
              type="password"
              id="confirmPassword"
              className={`form-control ${errors.confirmPassword ? 'is-invalid' : ''}`}
              {...register('confirmPassword')}
              placeholder="Confirm your password"
            />
            {errors.confirmPassword && (
              <div className="form-error">{errors.confirmPassword.message}</div>
            )}
          </div>
          
          <div className="form-group">
            <label htmlFor="address" className="form-label">
              Address (Optional)
            </label>
            <textarea
              id="address"
              className={`form-control ${errors.address ? 'is-invalid' : ''}`}
              {...register('address')}
              rows="3"
              placeholder="Enter your address"
            />
            {errors.address && (
              <div className="form-error">{errors.address.message}</div>
            )}
          </div>
          
          <button
            type="submit"
            className="btn btn-primary btn-block"
            disabled={loading}
          >
            {loading ? 'Registering...' : 'Register'}
          </button>
          
          <div className="auth-footer">
            Already have an account?{' '}
            <Link to="/login" className="auth-link">
              Login
            </Link>
          </div>
        </form>
      </div>
      
      <style jsx="true">{`
        .auth-container {
          display: flex;
          justify-content: center;
          align-items: center;
          min-height: calc(100vh - 200px);
          padding: 2rem 1rem;
        }
        
        .auth-card {
          background-color: white;
          border-radius: var(--border-radius);
          box-shadow: var(--shadow);
          padding: 2rem;
          width: 100%;
          max-width: 480px;
        }
        
        .auth-title {
          margin-bottom: 0.5rem;
          font-size: 1.8rem;
          color: var(--text-color);
        }
        
        .auth-subtitle {
          color: var(--light-text);
          margin-bottom: 2rem;
        }
        
        .auth-error {
          background-color: rgba(231, 76, 60, 0.1);
          color: var(--error-color);
          padding: 0.75rem;
          border-radius: var(--border-radius);
          margin-bottom: 1.5rem;
        }
        
        .auth-form {
          margin-bottom: 1rem;
        }
        
        .btn-block {
          width: 100%;
          margin: 1.5rem 0;
        }
        
        .auth-footer {
          text-align: center;
          margin-top: 1rem;
          color: var(--light-text);
        }
        
        .auth-link {
          color: var(--primary-color);
          font-weight: 500;
        }
        
        .is-invalid {
          border-color: var(--error-color);
        }
      `}</style>
    </div>
  );
};

export default Register;
