import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const StoreDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [store, setStore] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');

  useEffect(() => {
    const fetchStoreDetails = async () => {
      try {
        const response = await axios.get(`/api/stores/${id}`);
        setStore(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load store details');
        setLoading(false);
      }
    };

    fetchStoreDetails();
  }, [id]);

  const handleSubmitRating = async (e) => {
    e.preventDefault();
    if (!user) {
      navigate('/login');
      return;
    }

    try {
      await axios.post(`/api/stores/${id}/ratings`, {
        rating,
        comment
      });
      // Refresh store details to show new rating
      const response = await axios.get(`/api/stores/${id}`);
      setStore(response.data);
      setRating(0);
      setComment('');
    } catch (err) {
      setError('Failed to submit rating');
    }
  };

  if (loading) return <div className="container mt-4">Loading...</div>;
  if (error) return <div className="container mt-4 text-danger">{error}</div>;
  if (!store) return <div className="container mt-4">Store not found</div>;

  return (
    <div className="container mt-4">
      <div className="card">
        <div className="card-body">
          <h1 className="card-title">{store.name}</h1>
          <p className="card-text">{store.description}</p>
          <div className="mb-3">
            <strong>Address:</strong> {store.address}
          </div>
          <div className="mb-3">
            <strong>Average Rating:</strong> {store.averageRating?.toFixed(1) || 'No ratings yet'}
          </div>
          
          {/* Rating Form */}
          {user && (
            <form onSubmit={handleSubmitRating} className="mt-4">
              <h3>Rate this store</h3>
              <div className="mb-3">
                <label className="form-label">Rating</label>
                <select 
                  className="form-select" 
                  value={rating} 
                  onChange={(e) => setRating(Number(e.target.value))}
                  required
                >
                  <option value="0">Select rating</option>
                  {[1, 2, 3, 4, 5].map(num => (
                    <option key={num} value={num}>{num} stars</option>
                  ))}
                </select>
              </div>
              <div className="mb-3">
                <label className="form-label">Comment</label>
                <textarea 
                  className="form-control" 
                  value={comment} 
                  onChange={(e) => setComment(e.target.value)}
                  rows="3"
                  required
                />
              </div>
              <button type="submit" className="btn btn-primary">Submit Rating</button>
            </form>
          )}
          
          {/* Ratings List */}
          <div className="mt-4">
            <h3>Recent Ratings</h3>
            {store.ratings && store.ratings.length > 0 ? (
              <div className="list-group">
                {store.ratings.map(rating => (
                  <div key={rating.id} className="list-group-item">
                    <div className="d-flex justify-content-between">
                      <div>
                        <strong>{rating.user?.username || 'Anonymous'}</strong>
                        <div className="text-warning">
                          {'★'.repeat(rating.rating)}{'☆'.repeat(5 - rating.rating)}
                        </div>
                      </div>
                      <small className="text-muted">
                        {new Date(rating.createdAt).toLocaleDateString()}
                      </small>
                    </div>
                    <p className="mb-0 mt-2">{rating.comment}</p>
                  </div>
                ))}
              </div>
            ) : (
              <p>No ratings yet</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default StoreDetails;
