import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const StoreOwnerDashboard = () => {
  const { user } = useAuth();
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [newStore, setNewStore] = useState({
    name: '',
    address: '',
    description: ''
  });

  useEffect(() => {
    fetchStores();
  }, []);

  const fetchStores = async () => {
    try {
      const response = await axios.get('/api/stores/owner');
      setStores(response.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to load stores');
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setNewStore({
      ...newStore,
      [e.target.name]: e.target.value
    });
  };

  const handleAddStore = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/stores', newStore);
      setMessage({ type: 'success', text: 'Store added successfully and pending approval' });
      setNewStore({ name: '', address: '', description: '' });
      fetchStores(); // Refresh the store list
    } catch (err) {
      setMessage({ 
        type: 'danger', 
        text: err.response?.data?.message || 'Failed to add store' 
      });
    }
  };

  const handleDeleteStore = async (storeId) => {
    if (!window.confirm('Are you sure you want to delete this store?')) {
      return;
    }

    try {
      await axios.delete(`/api/stores/${storeId}`);
      setMessage({ type: 'success', text: 'Store deleted successfully' });
      fetchStores(); // Refresh the store list
    } catch (err) {
      setMessage({ 
        type: 'danger', 
        text: err.response?.data?.message || 'Failed to delete store' 
      });
    }
  };

  if (loading) return <div className="container mt-4">Loading...</div>;
  if (error) return <div className="container mt-4 text-danger">{error}</div>;

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Store Owner Dashboard</h1>
      <p className="lead">Welcome, {user?.username}! Manage your stores here.</p>

      {message.text && (
        <div className={`alert alert-${message.type} alert-dismissible fade show`} role="alert">
          {message.text}
          <button 
            type="button" 
            className="btn-close" 
            onClick={() => setMessage({ type: '', text: '' })}
            aria-label="Close"
          />
        </div>
      )}

      {/* Add New Store Form */}
      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">Add New Store</h5>
          <form onSubmit={handleAddStore}>
            <div className="row">
              <div className="col-md-4 mb-3">
                <label htmlFor="name" className="form-label">Store Name</label>
                <input
                  type="text"
                  className="form-control"
                  id="name"
                  name="name"
                  value={newStore.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-4 mb-3">
                <label htmlFor="address" className="form-label">Address</label>
                <input
                  type="text"
                  className="form-control"
                  id="address"
                  name="address"
                  value={newStore.address}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-4 mb-3">
                <label htmlFor="description" className="form-label">Description</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  name="description"
                  value={newStore.description}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            <button type="submit" className="btn btn-primary">Add Store</button>
          </form>
        </div>
      </div>

      {/* Store List */}
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">Your Stores</h5>
          {stores.length === 0 ? (
            <p>You haven't added any stores yet.</p>
          ) : (
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Status</th>
                    <th>Rating</th>
                    <th>Created At</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {stores.map(store => (
                    <tr key={store.id}>
                      <td>
                        <Link to={`/stores/${store.id}`}>{store.name}</Link>
                      </td>
                      <td>{store.address}</td>
                      <td>
                        <span className={`badge bg-${store.approved ? 'success' : 'warning'}`}>
                          {store.approved ? 'Approved' : 'Pending Approval'}
                        </span>
                      </td>
                      <td>
                        <div className="text-warning">
                          {'★'.repeat(Math.round(store.averageRating || 0))}
                          {'☆'.repeat(5 - Math.round(store.averageRating || 0))}
                        </div>
                        <small className="text-muted">
                          {store.averageRating ? `${store.averageRating.toFixed(1)} (${store.ratingCount} ratings)` : 'No ratings yet'}
                        </small>
                      </td>
                      <td>{new Date(store.createdAt).toLocaleDateString()}</td>
                      <td>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => handleDeleteStore(store.id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default StoreOwnerDashboard;
