import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaStar } from 'react-icons/fa';

const StoreOwnerDashboard = () => {
  const [storeRatings, setStoreRatings] = useState({
    average: 0,
    total: 0,
    ratingsList: []
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchStoreRatings();
  }, []);

  const fetchStoreRatings = async () => {
    try {
      const response = await axios.get('/api/store/ratings');
      const data = response.data;
      setStoreRatings({
        average: data.averageRating,
        total: data.totalRatings,
        ratingsList: data.ratings
      });
    } catch (err) {
      navigate('/login');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const handlePasswordChange = () => {
    navigate('/change-password');
  };

  return (
    <div className="container py-4">
      {/* Essential Actions */}
      <div className="text-end mb-4">
        <button onClick={handlePasswordChange} className="btn btn-primary me-2">
          Change Password
        </button>
        <button onClick={handleLogout} className="btn btn-danger">
          Logout
        </button>
      </div>

      {/* Store Rating */}
      <div className="card text-center mb-4">
        <div className="card-body">
          <h3>Store Rating</h3>
          <div className="display-1">
            {storeRatings.average.toFixed(1)}
            <FaStar className="text-warning ms-2" style={{ fontSize: '0.8em' }} />
          </div>
          <div className="text-muted">
            {storeRatings.total} total ratings
          </div>
        </div>
      </div>

      {/* Ratings Table */}
      <div className="card">
        <div className="card-header">
          <h4 className="m-0">Customer Ratings</h4>
        </div>
        <div className="table-responsive">
          <table className="table table-hover mb-0">
            <thead>
              <tr>
                <th>User</th>
                <th>Rating</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {storeRatings.ratingsList.map(rating => (
                <tr key={rating.id}>
                  <td>{rating.username}</td>
                  <td>
                    {rating.rating} <FaStar className="text-warning" />
                  </td>
                  <td>
                    {new Date(rating.date).toLocaleDateString()}
                  </td>
                </tr>
              ))}
              {!storeRatings.ratingsList.length && (
                <tr>
                  <td colSpan="3" className="text-center py-3">
                    No ratings available
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default StoreOwnerDashboard;
