import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SearchBox from '../components/SearchBox';
import { FaUsers } from 'react-icons/fa';

const AdminDashboard = () => {
  const [userFilters, setUserFilters] = useState({
    name: '',
    email: '',
    role: ''
  });
  
  const [stats, setStats] = useState({
    totalUsers: 0
  });
  
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch users statistics
  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await axios.get('/api/admin/users/stats');
        setStats(response.data);
      } catch (err) {
        console.error('Error fetching stats:', err);
      }
    };
    fetchStats();
  }, []);

  const handleSearch = async (filters) => {
    setLoading(true);
    setError(null);
    
    try {
      const searchParams = Object.fromEntries(
        Object.entries(filters).filter(([_, value]) => value !== '')
      );

      const response = await axios.get('/api/admin/users/search', {
        params: searchParams
      });
      setUsers(response.data);
    } catch (err) {
      setError('Error searching users. Please try again.');
      console.error('Search error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-4">
      <h1 className="mb-4">User Management</h1>
      
      {/* Statistics Card */}
      <div className="row mb-4">
        <div className="col-md-4">
          <div className="card bg-primary text-white">
            <div className="card-body">
              <div className="d-flex justify-content-between align-items-center">
                <div>
                  <h6 className="card-title">Total Users</h6>
                  <h2 className="mb-0">{stats.totalUsers}</h2>
                </div>
                <FaUsers size={40} opacity={0.5} />
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Search Box */}
      <SearchBox 
        filters={userFilters} 
        setFilters={setUserFilters}
        type="user"
        onSearch={handleSearch}
      />

      {/* Loading Spinner */}
      {loading && (
        <div className="text-center my-4">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      )}

      {/* Error Alert */}
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      {/* Users Table */}
      <div className="card mt-4">
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-hover">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Role</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.length > 0 ? (
                  users.map(user => (
                    <tr key={user._id}>
                      <td>{user.name}</td>
                      <td>{user.email}</td>
                      <td>
                        <span className={`badge bg-${user.role === 'admin' ? 'danger' : 'primary'}`}>
                          {user.role}
                        </span>
                      </td>
                      <td>
                        <button 
                          className="btn btn-outline-danger btn-sm"
                          onClick={() => {
                            if(window.confirm('Are you sure you want to delete this user?')) {
                              // Add delete functionality
                            }
                          }}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="4" className="text-center">
                      <p className="text-muted my-3">No users found matching your criteria</p>
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
