import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FaUsers, FaStore, FaStar, FaUserShield, FaUserTag, FaChartLine } from 'react-icons/fa';
import SearchBox from '../components/SearchBox';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalStores: 0,
    totalRatings: 0,
    usersByRole: {
      admin: 0,
      store_owner: 0,
      user: 0
    },
    recentUsers: [],
    recentStores: [],
    topRatedStores: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [storeFilters, setStoreFilters] = useState({
    name: '',
    address: ''
  });
  const [userFilters, setUserFilters] = useState({
    email: '',
    role: ''
  });
  const [filteredStores, setFilteredStores] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await axios.get('/api/admin/dashboard');
        setStats(response.data);
      } catch (err) {
        console.error('Error fetching dashboard stats:', err);
        setError('Failed to load dashboard statistics');
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  useEffect(() => {
    const fetchFilteredData = async () => {
      try {
        // Fetch stores based on filters
        const storeResponse = await axios.get('/api/admin/stores/search', {
          params: storeFilters
        });
        setFilteredStores(storeResponse.data);

        // Fetch users based on filters
        const userResponse = await axios.get('/api/admin/users/search', {
          params: userFilters
        });
        setFilteredUsers(userResponse.data);
      } catch (error) {
        console.error('Error fetching filtered data:', error);
      }
    };

    fetchFilteredData();
  }, [storeFilters, userFilters]);

  const handleStoreSearch = (e) => {
    const { name, value } = e.target;
    setStoreFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleUserSearch = (e) => {
    const { name, value } = e.target;
    setUserFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  if (loading) return <div className="container mt-4">Loading dashboard data...</div>;

  if (error) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger">{error}</div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Admin Dashboard</h1>
      
      {/* Store Search */}
      <SearchBox 
        filters={storeFilters} 
        setFilters={setStoreFilters}
        type="store"
      />

      {/* User Search */}
      <SearchBox 
        filters={userFilters} 
        setFilters={setUserFilters}
        type="user"
      />

      {/* Statistics Cards */}
      <div className="row mb-4">
        <div className="col-md-4 mb-3">
          <div className="card h-100 bg-primary text-white">
            <div className="card-body d-flex align-items-center">
              <FaUsers className="me-3" style={{ fontSize: '2.5rem' }} />
              <div>
                <h5 className="card-title">Total Users</h5>
                <h2 className="mb-0">{stats.totalUsers}</h2>
              </div>
            </div>
            <div className="card-footer bg-transparent border-0">
              <Link to="/dashboard/admin/users" className="text-white">View All Users →</Link>
            </div>
          </div>
        </div>
        
        <div className="col-md-4 mb-3">
          <div className="card h-100 bg-success text-white">
            <div className="card-body d-flex align-items-center">
              <FaStore className="me-3" style={{ fontSize: '2.5rem' }} />
              <div>
                <h5 className="card-title">Total Stores</h5>
                <h2 className="mb-0">{stats.totalStores}</h2>
              </div>
            </div>
            <div className="card-footer bg-transparent border-0">
              <Link to="/dashboard/admin/stores" className="text-white">View All Stores →</Link>
            </div>
          </div>
        </div>
        
        <div className="col-md-4 mb-3">
          <div className="card h-100 bg-warning text-white">
            <div className="card-body d-flex align-items-center">
              <FaStar className="me-3" style={{ fontSize: '2.5rem' }} />
              <div>
                <h5 className="card-title">Total Ratings</h5>
                <h2 className="mb-0">{stats.totalRatings}</h2>
              </div>
            </div>
            <div className="card-footer bg-transparent border-0">
              <Link to="/dashboard/admin/ratings" className="text-white">View All Ratings →</Link>
            </div>
          </div>
        </div>
      </div>
      
      {/* User Role Distribution */}
      <div className="row mb-4">
        <div className="col-md-12">
          <div className="card">
            <div className="card-header">
              <h5 className="card-title mb-0">User Role Distribution</h5>
            </div>
            <div className="card-body">
              <div className="row text-center">
                <div className="col-md-4">
                  <div className="p-3">
                    <FaUserShield className="mb-3" style={{ fontSize: '2rem', color: '#4a90e2' }} />
                    <h4>{stats.usersByRole.admin || 0}</h4>
                    <h6 className="text-muted">Administrators</h6>
                  </div>
                </div>
                
                <div className="col-md-4">
                  <div className="p-3">
                    <FaStore className="mb-3" style={{ fontSize: '2rem', color: '#50c878' }} />
                    <h4>{stats.usersByRole.store_owner || 0}</h4>
                    <h6 className="text-muted">Store Owners</h6>
                  </div>
                </div>
                
                <div className="col-md-4">
                  <div className="p-3">
                    <FaUserTag className="mb-3" style={{ fontSize: '2rem', color: '#f8c630' }} />
                    <h4>{stats.usersByRole.user || 0}</h4>
                    <h6 className="text-muted">Regular Users</h6>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Recent Activity */}
      <div className="row">
        {/* Recent Users */}
        <div className="col-md-6 mb-4">
          <div className="card h-100">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h5 className="card-title mb-0">Recent Users</h5>
              <Link to="/dashboard/admin/users" className="btn btn-sm btn-primary">View All</Link>
            </div>
            <div className="card-body p-0">
              <div className="list-group list-group-flush">
                {stats.recentUsers && stats.recentUsers.length > 0 ? (
                  stats.recentUsers.map(user => (
                    <div key={user.id} className="list-group-item">
                      <div className="d-flex justify-content-between align-items-center">
                        <div>
                          <h6 className="mb-0">{user.username}</h6>
                          <small className="text-muted">{user.email}</small>
                        </div>
                        <span className={`badge bg-${user.role === 'admin' ? 'primary' : user.role === 'store_owner' ? 'success' : 'secondary'}`}>
                          {user.role === 'store_owner' ? 'Store Owner' : user.role === 'admin' ? 'Admin' : 'User'}
                        </span>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="list-group-item text-center py-4">
                    <p className="mb-0 text-muted">No recent users</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
        
        {/* Recent Stores */}
        <div className="col-md-6 mb-4">
          <div className="card h-100">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h5 className="card-title mb-0">Top Rated Stores</h5>
              <Link to="/dashboard/admin/stores" className="btn btn-sm btn-primary">View All</Link>
            </div>
            <div className="card-body p-0">
              <div className="list-group list-group-flush">
                {(storeFilters.name || storeFilters.address ? filteredStores : stats.topRatedStores)?.length > 0 ? (
                  (storeFilters.name || storeFilters.address ? filteredStores : stats.topRatedStores).map(store => (
                    <div key={store.id} className="list-group-item">
                      <div className="d-flex justify-content-between align-items-center">
                        <div>
                          <h6 className="mb-0">{store.name}</h6>
                          <small className="text-muted">{store.address}</small>
                        </div>
                        <div className="text-warning">
                          <span className="me-1">{store.averageRating?.toFixed(1) || 'N/A'}</span>
                          <FaStar />
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="list-group-item text-center py-4">
                    <p className="mb-0 text-muted">
                      {storeFilters.name || storeFilters.address ? 'No stores found matching your search' : 'No store ratings yet'}
                    </p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Quick Links */}
      <div className="row mb-4">
        <div className="col-md-12">
          <div className="card">
            <div className="card-header">
              <h5 className="card-title mb-0">Quick Actions</h5>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-md-3 mb-3">
                  <Link to="/dashboard/admin/users/add" className="btn btn-outline-primary w-100 p-3">
                    <FaUsers className="mb-2" style={{ fontSize: '1.5rem' }} />
                    <div>Add New User</div>
                  </Link>
                </div>
                <div className="col-md-3 mb-3">
                  <Link to="/dashboard/admin/stores/add" className="btn btn-outline-success w-100 p-3">
                    <FaStore className="mb-2" style={{ fontSize: '1.5rem' }} />
                    <div>Add New Store</div>
                  </Link>
                </div>
                <div className="col-md-3 mb-3">
                  <Link to="/dashboard/admin/users" className="btn btn-outline-info w-100 p-3">
                    <FaUserShield className="mb-2" style={{ fontSize: '1.5rem' }} />
                    <div>Manage Users</div>
                  </Link>
                </div>
                <div className="col-md-3 mb-3">
                  <Link to="/dashboard/admin/stores" className="btn btn-outline-warning w-100 p-3">
                    <FaChartLine className="mb-2" style={{ fontSize: '1.5rem' }} />
                    <div>Manage Stores</div>
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
