import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaStar } from 'react-icons/fa';

const StoreOwnerDashboard = () => {
  const [ratings, setRatings] = useState({
    average: 0,
    total: 0,
    list: []
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRatings = async () => {
      try {
        const response = await axios.get('/api/ratings/store');
        if (response.data) {
          setRatings({
            average: response.data.averageRating || 0,
            total: response.data.totalRatings || 0,
            list: response.data.ratings || []
          });
        }
      } catch (err) {
        if (err.response?.status === 401) {
          navigate('/login');
        }
      }
    };

    fetchRatings();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="container mt-4">
      {/* Header with auth options */}
      <div className="d-flex justify-content-end mb-4">
        <Link to="/change-password" className="btn btn-primary me-2">
          Change Password
        </Link>
        <button onClick={handleLogout} className="btn btn-danger">
          Logout
        </button>
      </div>

      {/* Rating Summary */}
      <div className="card mb-4">
        <div className="card-body text-center">
          <div className="display-4">
            {ratings.average.toFixed(1)}
            <FaStar className="ms-2 text-warning" />
          </div>
          <p className="text-muted mb-0">
            Based on {ratings.total} rating{ratings.total !== 1 ? 's' : ''}
          </p>
        </div>
      </div>

      {/* Ratings List */}
      <div className="card">
        <div className="card-header bg-white">
          <h5 className="mb-0">Customer Ratings</h5>
        </div>
        <div className="table-responsive">
          <table className="table mb-0">
            <thead>
              <tr>
                <th>Customer</th>
                <th>Rating</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {ratings.list.length > 0 ? (
                ratings.list.map(rating => (
                  <tr key={rating.id}>
                    <td>{rating.username}</td>
                    <td>
                      {[...Array(5)].map((_, i) => (
                        <FaStar
                          key={i}
                          className={
                            i < rating.rating ? 'text-warning' : 'text-muted'
                          }
                        />
                      ))}
                    </td>
                    <td>{new Date(rating.date).toLocaleDateString()}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="3" className="text-center py-4">
                    No ratings yet
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default StoreOwnerDashboard;
