import React, { useState } from 'react';
import axios from 'axios'; // or Firebase/firestore depending on backend

function AddStoreOwner() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('');

  const handleAddOwner = async () => {
    if (!username || !email) {
      setStatus('Please fill in both fields.');
      return;
    }

    try {
      // If using Firebase, replace this with Firestore code.
      // Example with Axios for a REST API:
      const response = await axios.post('https://your-backend-api.com/add-store-owner', {
        username,
        email,
      });

      if (response.status === 200) {
        setStatus('Store owner added successfully!');
        setUsername('');
        setEmail('');
      } else {
        setStatus('Failed to add store owner.');
      }
    } catch (error) {
      console.error('Error adding store owner:', error);
      setStatus('Error: Could not add store owner.');
    }
  };

  return (
    <div className="p-4 max-w-md mx-auto bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Add New Store Owner</h2>

      <input
        type="text"
        placeholder="Store owner's username (e.g., john_store123)"
        className="form-control w-full mb-3 border px-3 py-2 rounded"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />

      <input
        type="email"
        placeholder="Store owner's email (e.g., john@example.com)"
        className="form-control w-full mb-3 border px-3 py-2 rounded"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />

      <button
        className="bg-blue-600 text-white px-4 py-2 rounded"
        onClick={handleAddOwner}
      >
        Add Owner
      </button>

      {status && <p className="mt-3 text-sm text-gray-700">{status}</p>}
    </div>
  );
}

export default AddStoreOwner;
