import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const StoreList = () => {
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('rating'); // 'rating' or 'name'

  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await axios.get('/api/stores');
        setStores(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load stores');
        setLoading(false);
      }
    };

    fetchStores();
  }, []);

  const filteredStores = stores
    .filter(store => 
      store.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      store.address.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .sort((a, b) => {
      if (sortBy === 'rating') {
        return (b.averageRating || 0) - (a.averageRating || 0);
      }
      return a.name.localeCompare(b.name);
    });

  if (loading) return <div className="container mt-4">Loading...</div>;
  if (error) return <div className="container mt-4 text-danger">{error}</div>;

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Stores</h1>
        <div className="d-flex gap-2">
          <input
            type="text"
            className="form-control"
            placeholder="Search stores..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <select 
            className="form-select" 
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            style={{ width: 'auto' }}
          >
            <option value="rating">Sort by Rating</option>
            <option value="name">Sort by Name</option>
          </select>
        </div>
      </div>

      {filteredStores.length === 0 ? (
        <div className="alert alert-info">
          No stores found matching your search criteria.
        </div>
      ) : (
        <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
          {filteredStores.map(store => (
            <div key={store.id} className="col">
              <div className="card h-100">
                <div className="card-body">
                  <h5 className="card-title">{store.name}</h5>
                  <p className="card-text text-muted">{store.address}</p>
                  <div className="mb-2">
                    <div className="text-warning">
                      {'★'.repeat(Math.round(store.averageRating || 0))}
                      {'☆'.repeat(5 - Math.round(store.averageRating || 0))}
                    </div>
                    <small className="text-muted">
                      {store.averageRating ? `${store.averageRating.toFixed(1)} (${store.ratingCount} ratings)` : 'No ratings yet'}
                    </small>
                  </div>
                  <Link to={`/stores/${store.id}`} className="btn btn-primary">
                    View Details
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default StoreList;
