import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const AdminAddStore = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const isEditMode = !!id;

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [message, setMessage] = useState({ type: '', text: '' });
  const [storeOwners, setStoreOwners] = useState([]);
  const [loadingOwners, setLoadingOwners] = useState(true);
  const [loadingStore, setLoadingStore] = useState(isEditMode);
  const [newStoreOwner, setNewStoreOwner] = useState(false);
  const [storeOwnerData, setStoreOwnerData] = useState({
    username: '',
    email: '',
    password: 'owner123'
  });

  const [formData, setFormData] = useState({
    name: '',
    address: '',
    description: '',
    imageUrl: '',
    category: '',
    ownerId: '',
    approved: true,
    openingHours: '',
    contactEmail: '',
    contactPhone: ''
  });

  useEffect(() => {
    const fetchStoreOwners = async () => {
      try {
        const response = await axios.get('/api/admin/users', {
          params: { role: 'store_owner' }
        });
        setStoreOwners(Array.isArray(response.data) ? response.data : response.data.users || []);
      } catch (err) {
        console.error('Error fetching store owners:', err);
      } finally {
        setLoadingOwners(false);
      }
    };
    fetchStoreOwners();
  }, []);

  useEffect(() => {
    if (isEditMode) {
      const fetchStore = async () => {
        try {
          const response = await axios.get(`/api/admin/stores/${id}`);
          const storeData = response.data;
          setFormData({
            name: storeData.name || '',
            address: storeData.address || '',
            description: storeData.description || '',
            imageUrl: storeData.imageUrl || '',
            category: storeData.category || '',
            ownerId: storeData.ownerId || '',
            approved: storeData.approved !== false,
            openingHours: storeData.openingHours || '',
            contactEmail: storeData.contactEmail || '',
            contactPhone: storeData.contactPhone || ''
          });
        } catch (err) {
          console.error('Error fetching store:', err);
          setError('Failed to load store data.');
        } finally {
          setLoadingStore(false);
        }
      };
      fetchStore();
    }
  }, [id, isEditMode]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({ ...formData, [name]: type === 'checkbox' ? checked : value });
  };

  const handleStoreOwnerChange = (e) => {
    const { name, value } = e.target;
    setStoreOwnerData({ ...storeOwnerData, [name]: value });
  };

  const createStoreOwner = async () => {
    let email = storeOwnerData.email;
    if (!email.endsWith('@owner.ratemystore.com')) {
      email = email.split('@')[0] + '@owner.ratemystore.com';
    }
    const userData = {
      username: storeOwnerData.username,
      email,
      password: storeOwnerData.password,
      role: 'store_owner'
    };
    const response = await axios.post('/api/admin/users', userData);
    return response.data.user.id;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      let ownerId = formData.ownerId;
      if (newStoreOwner) {
        try {
          ownerId = await createStoreOwner();
        } catch (err) {
          setError(`Failed to create store owner: ${err.message}`);
          setLoading(false);
          return;
        }
      }
      const storeData = { ...formData, ownerId };
      let response;
      if (isEditMode) {
        response = await axios.put(`/api/admin/stores/${id}`, storeData);
        setMessage({ type: 'success', text: `Store "${response.data.name}" updated successfully!` });
      } else {
        response = await axios.post('/api/admin/stores', storeData);
        setMessage({ type: 'success', text: `Store "${response.data.name}" created successfully!${newStoreOwner ? ' A new store owner account was also created.' : ''}` });
        setFormData({ name: '', address: '', description: '', imageUrl: '', category: '', ownerId: '', approved: true, openingHours: '', contactEmail: '', contactPhone: '' });
        setNewStoreOwner(false);
        setStoreOwnerData({ username: '', email: '', password: 'owner123' });
      }
      setTimeout(() => navigate('/dashboard/admin/stores'), 2000);
    } catch (err) {
      setError(err.response?.data?.message || `Failed to ${isEditMode ? 'update' : 'create'} store.`);
    } finally {
      setLoading(false);
    }
  };

  const toggleNewStoreOwnerForm = () => {
    setNewStoreOwner(!newStoreOwner);
  };

  if (loadingStore) return <div className="container mt-4">Loading store data...</div>;

  return (
    <div className="container mt-4">
      <h1 className="mb-4">{isEditMode ? 'Edit Store' : 'Add New Store'}</h1>
      {message.text && (
        <div className={`alert alert-${message.type}`} role="alert">
          {message.text}
        </div>
      )}
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Store Name</label>
          <input type="text" className="form-control" name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label className="form-label">Category</label>
          <select className="form-select" name="category" value={formData.category} onChange={handleChange} required>
            <option value="">Select a category</option>
            <option value="Restaurant">Restaurant</option>
            <option value="Cafe">Cafe</option>
            <option value="Retail">Retail</option>
            <option value="Electronics">Electronics</option>
            <option value="Clothing">Clothing</option>
            <option value="Grocery">Grocery</option>
            <option value="Entertainment">Entertainment</option>
            <option value="Services">Services</option>
            <option value="Other">Other</option>
          </select>
        </div>
        <div className="mb-3">
          <label className="form-label">Address</label>
          <textarea className="form-control" name="address" value={formData.address} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label className="form-label">Description</label>
          <textarea className="form-control" name="description" value={formData.description} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Opening Hours</label>
          <input type="text" className="form-control" name="openingHours" value={formData.openingHours} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Image URL</label>
          <input type="url" className="form-control" name="imageUrl" value={formData.imageUrl} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Contact Email</label>
          <input type="email" className="form-control" name="contactEmail" value={formData.contactEmail} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Contact Phone</label>
          <input type="text" className="form-control" name="contactPhone" value={formData.contactPhone} onChange={handleChange} />
        </div>

        <div className="form-check mb-3">
          <input className="form-check-input" type="checkbox" checked={formData.approved} name="approved" onChange={handleChange} />
          <label className="form-check-label">Approved</label>
        </div>

        <div className="mb-3">
          <button type="button" className="btn btn-secondary mb-2" onClick={toggleNewStoreOwnerForm}>
            {newStoreOwner ? 'Select Existing Owner' : 'Create New Store Owner'}
          </button>

          {newStoreOwner ? (
            <div>
              <input type="text" className="form-control mb-2" placeholder="Username" name="username" value={storeOwnerData.username} onChange={handleStoreOwnerChange} required />
              <input type="email" className="form-control mb-2" placeholder="Email" name="email" value={storeOwnerData.email} onChange={handleStoreOwnerChange} required />
            </div>
          ) : (
            <select className="form-select" name="ownerId" value={formData.ownerId} onChange={handleChange} required>
              <option value="">Select Store Owner</option>
              {storeOwners.map(owner => (
                <option key={owner.id} value={owner.id}>{owner.username} ({owner.email})</option>
              ))}
            </select>
          )}
        </div>

        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? 'Saving...' : isEditMode ? 'Update Store' : 'Create Store'}
        </button>
      </form>
    </div>
  );
};

export default AdminAddStore;
