import React from 'react';
import { Link } from 'react-router-dom';
import { FaStar, FaRegStar, FaStarHalfAlt, FaMapMarkerAlt } from 'react-icons/fa';

const StoreCard = ({ store }) => {
  // Generate stars based on rating
  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 >= 0.5;
    
    // Add full stars
    for (let i = 0; i < fullStars; i++) {
      stars.push(<FaStar key={`full-${i}`} className="rating-star filled" />);
    }
    
    // Add half star if needed
    if (hasHalfStar) {
      stars.push(<FaStarHalfAlt key="half" className="rating-star filled" />);
    }
    
    // Add empty stars
    const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);
    for (let i = 0; i < emptyStars; i++) {
      stars.push(<FaRegStar key={`empty-${i}`} className="rating-star" />);
    }
    
    return stars;
  };
  
  return (
    <div className="store-card">
      <div className="store-card-body">
        <h3 className="store-name">
          <Link to={`/stores/${store.id}`}>{store.name}</Link>
        </h3>
        
        <div className="store-address">
          <FaMapMarkerAlt className="address-icon" />
          <span>{store.address}</span>
        </div>
        
        <div className="store-rating">
          <div className="stars">{renderStars(store.averageRating)}</div>
          <span className="rating-value">{store.averageRating.toFixed(1)}</span>
          <span className="total-ratings">({store.totalRatings} ratings)</span>
        </div>
        
        {store.userRating !== null && (
          <div className="user-rating">
            <span className="user-rating-label">Your rating:</span>
            <div className="stars">{renderStars(store.userRating)}</div>
            <span className="rating-value">{store.userRating.toFixed(1)}</span>
          </div>
        )}
      </div>
      
      <div className="store-card-footer">
        <Link to={`/stores/${store.id}`} className="btn btn-outline">
          View Details
        </Link>
      </div>
      
      <style jsx="true">{`
        .store-card {
          background-color: white;
          border-radius: var(--border-radius);
          box-shadow: var(--shadow);
          transition: var(--transition);
          height: 100%;
          display: flex;
          flex-direction: column;
        }
        
        .store-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 4px 15px rgba(0, 0, 0, 0.15);
        }
        
        .store-card-body {
          padding: 1.5rem;
          flex: 1;
        }
        
        .store-name {
          font-size: 1.3rem;
          margin-bottom: 0.5rem;
        }
        
        .store-name a {
          color: var(--text-color);
          text-decoration: none;
          transition: var(--transition);
        }
        
        .store-name a:hover {
          color: var(--primary-color);
        }
        
        .store-address {
          display: flex;
          align-items: center;
          margin-bottom: 1rem;
          color: var(--light-text);
          font-size: 0.9rem;
        }
        
        .address-icon {
          color: var(--accent-color);
          margin-right: 0.5rem;
        }
        
        .store-rating {
          display: flex;
          align-items: center;
          margin-bottom: 1rem;
        }
        
        .stars {
          display: flex;
          margin-right: 0.5rem;
        }
        
        .rating-star {
          color: var(--medium-gray);
          margin-right: 2px;
        }
        
        .rating-star.filled {
          color: var(--accent-color);
        }
        
        .rating-value {
          font-weight: 600;
          margin-right: 0.25rem;
        }
        
        .total-ratings {
          font-size: 0.85rem;
          color: var(--light-text);
        }
        
        .user-rating {
          display: flex;
          align-items: center;
          background-color: var(--lightest-gray);
          padding: 0.5rem;
          border-radius: var(--border-radius);
        }
        
        .user-rating-label {
          margin-right: 0.5rem;
          font-size: 0.85rem;
          font-weight: 500;
        }
        
        .store-card-footer {
          padding: 1rem 1.5rem;
          border-top: 1px solid var(--light-gray);
          text-align: right;
        }
      `}</style>
    </div>
  );
};

export default StoreCard;
