import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { FaStar, FaKey, FaSignOutAlt } from 'react-icons/fa';

const StoreOwnerDashboard = () => {
  const { user, logout } = useAuth();
  const [storeData, setStoreData] = useState({
    ratings: [],
    averageRating: 0,
    totalRatings: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStoreRatings = async () => {
      try {
        const response = await axios.get('/api/stores/owner/ratings');
        setStoreData(response.data);
      } catch (err) {
        setError('Failed to load ratings data');
        console.error('Error:', err);
      } finally {
        setLoading(false);
      }
    };

    if (user?.role === 'store_owner') {
      fetchStoreRatings();
    }
  }, [user]);

  const handleLogout = async () => {
    try {
      await logout();
    } catch (err) {
      setError('Failed to logout');
    }
  };

  if (!user || user.role !== 'store_owner') {
    return (
      <div className="container mt-4">
        <div className="alert alert-warning">
          Please login as a store owner to access this page.
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      {/* Header with Logout and Change Password */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Store Owner Dashboard</h2>
        <div>
          <Link to="/change-password" className="btn btn-warning me-2">
            <FaKey className="me-2" />
            Change Password
          </Link>
          <button onClick={handleLogout} className="btn btn-danger">
            <FaSignOutAlt className="me-2" />
            Logout
          </button>
        </div>
      </div>

      {error && (
        <div className="alert alert-danger mb-4">
          {error}
        </div>
      )}

      {/* Average Rating Display */}
      <div className="card mb-4">
        <div className="card-body text-center">
          <h3>Store Rating</h3>
          <div className="display-4 mb-2">
            {storeData.averageRating?.toFixed(1) || '0.0'}
            <FaStar className="ms-2 text-warning" />
          </div>
          <p className="text-muted">
            Based on {storeData.totalRatings} ratings
          </p>
        </div>
      </div>

      {/* Ratings List */}
      <div className="card">
        <div className="card-header">
          <h5 className="mb-0">Customer Ratings</h5>
        </div>
        <div className="card-body">
          {storeData.ratings?.length > 0 ? (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Customer</th>
                    <th>Rating</th>
                    <th>Date</th>
                  </tr>
                </thead>
                <tbody>
                  {storeData.ratings.map(rating => (
                    <tr key={rating.id}>
                      <td>{rating.username}</td>
                      <td>
                        <div className="text-warning">
                          {'★'.repeat(rating.rating)}
                          {'☆'.repeat(5 - rating.rating)}
                        </div>
                      </td>
                      <td>
                        {new Date(rating.created_at).toLocaleDateString()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-center my-3">
              No ratings received yet.
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default StoreOwnerDashboard;
