import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AdminAddUser = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [message, setMessage] = useState({ type: '', text: '' });
  const [cardValid, setCardValid] = useState(true);
  
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'user',
    address: '',
    paymentInfo: {
      cardNumber: '',
      cardHolder: '',
      expiryDate: '',
      cvv: ''
    }
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData({
        ...formData,
        [parent]: {
          ...formData[parent],
          [child]: value
        }
      });
    } else {
      setFormData({
        ...formData,
        [name]: value
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate card number if it's not empty
    if (formData.paymentInfo.cardNumber) {
      const isValid = validateCardNumber(formData.paymentInfo.cardNumber.replace(/\s/g, ''));
      if (!isValid) {
        setCardValid(false);
        return; // Don't submit if card number is invalid
      }
    }
    
    setLoading(true);
    setError('');
    
    try {
      // Format data for API
      const userData = {
        ...formData,
        paymentInfo: formData.paymentInfo.cardNumber ? {
          cardNumberLast4: formData.paymentInfo.cardNumber.slice(-4),
          cardHolder: formData.paymentInfo.cardHolder,
          expiryDate: formData.paymentInfo.expiryDate
        } : null
      };
      
      // Remove sensitive data
      delete userData.paymentInfo.cardNumber;
      delete userData.paymentInfo.cvv;
      
      const response = await axios.post('/api/admin/users', userData);
      
      setMessage({ 
        type: 'success', 
        text: `User ${response.data.username} created successfully!` 
      });
      
      // Reset form after success
      setFormData({
        username: '',
        email: '',
        password: '',
        role: 'user',
        address: '',
        paymentInfo: {
          cardNumber: '',
          cardHolder: '',
          expiryDate: '',
          cvv: ''
        }
      });
      
      // Reset validation state
      setCardValid(true);
      
      // Redirect after 2 seconds
      setTimeout(() => {
        navigate('/admin/users');
      }, 2000);
      
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create user. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const validateCardNumber = (cardNumber) => {
    // Basic validation - only digits and proper length (13-19 digits)
    const digits = cardNumber.replace(/\D/g, '');
    return /^\d{13,19}$/.test(digits);
  };

  const formatCardNumber = (value) => {
    // Format as xxxx xxxx xxxx xxxx
    const digits = value.replace(/\D/g, '');
    const groups = [];
    
    for (let i = 0; i < digits.length; i += 4) {
      groups.push(digits.slice(i, i + 4));
    }
    
    return groups.join(' ');
  };

  const handleCardNumberChange = (e) => {
    let { value } = e.target;
    
    // Format the card number with spaces
    value = formatCardNumber(value);
    
    // Validate card number and set an error if needed
    const isValid = value === '' || validateCardNumber(value.replace(/\s/g, ''));
    setCardValid(isValid);
    
    setFormData({
      ...formData,
      paymentInfo: {
        ...formData.paymentInfo,
        cardNumber: value
      }
    });
  };

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Add New User</h1>
      
      {message.text && (
        <div className={`alert alert-${message.type} alert-dismissible fade show`} role="alert">
          {message.text}
          <button 
            type="button" 
            className="btn-close" 
            onClick={() => setMessage({ type: '', text: '' })}
            aria-label="Close"
          />
        </div>
      )}
      
      {error && (
        <div className="alert alert-danger mb-4">
          {error}
        </div>
      )}
      
      <div className="card">
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <h4>User Information</h4>
              <hr />
              
              <div className="row g-3">
                <div className="col-md-6">
                  <label htmlFor="username" className="form-label">Username *</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    id="username" 
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    required
                    placeholder="Enter username"
                  />
                </div>
                
                <div className="col-md-6">
                  <label htmlFor="email" className="form-label">Email *</label>
                  <input 
                    type="email" 
                    className="form-control" 
                    id="email" 
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    placeholder="Enter email"
                  />
                </div>
                
                <div className="col-md-6">
                  <label htmlFor="password" className="form-label">Password *</label>
                  <input 
                    type="password" 
                    className="form-control" 
                    id="password" 
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                    placeholder="Enter password"
                    minLength="6"
                  />
                </div>
                
                <div className="col-md-6">
                  <label htmlFor="role" className="form-label">Role *</label>
                  <select 
                    className="form-select" 
                    id="role" 
                    name="role"
                    value={formData.role}
                    onChange={handleChange}
                    required
                  >
                    <option value="user">Regular User</option>
                    <option value="store_owner">Store Owner</option>
                    <option value="admin">Administrator</option>
                  </select>
                </div>
                
                <div className="col-12">
                  <label htmlFor="address" className="form-label">Address</label>
                  <textarea 
                    className="form-control" 
                    id="address" 
                    name="address"
                    value={formData.address}
                    onChange={handleChange}
                    placeholder="Enter address"
                    rows="2"
                  />
                </div>
              </div>
            </div>
            
            <div className="mb-4">
              <h4>Payment Information (Optional)</h4>
              <p className="text-muted">Add payment method for this user</p>
              <hr />
              
              <div className="row g-3">
                <div className="col-md-6">
                  <label htmlFor="cardNumber" className="form-label">Card Number</label>
                  <input 
                    type="text" 
                    className={`form-control ${!cardValid && formData.paymentInfo.cardNumber ? 'is-invalid' : ''}`}
                    id="cardNumber" 
                    name="paymentInfo.cardNumber"
                    value={formData.paymentInfo.cardNumber}
                    onChange={handleCardNumberChange}
                    placeholder="xxxx xxxx xxxx xxxx"
                    maxLength="19"
                  />
                  {!cardValid && formData.paymentInfo.cardNumber && (
                    <div className="invalid-feedback">
                      Please enter a valid card number
                    </div>
                  )}
                </div>
                
                <div className="col-md-6">
                  <label htmlFor="cardHolder" className="form-label">Card Holder Name</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    id="cardHolder" 
                    name="paymentInfo.cardHolder"
                    value={formData.paymentInfo.cardHolder}
                    onChange={handleChange}
                    placeholder="Enter name on card"
                  />
                </div>
                
                <div className="col-md-4">
                  <label htmlFor="expiryDate" className="form-label">Expiry Date</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    id="expiryDate" 
                    name="paymentInfo.expiryDate"
                    value={formData.paymentInfo.expiryDate}
                    onChange={handleChange}
                    placeholder="MM/YY"
                    maxLength="5"
                  />
                </div>
                
                <div className="col-md-2">
                  <label htmlFor="cvv" className="form-label">CVV</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    id="cvv" 
                    name="paymentInfo.cvv"
                    value={formData.paymentInfo.cvv}
                    onChange={handleChange}
                    placeholder="123"
                    maxLength="4"
                  />
                </div>
              </div>
            </div>
            
            <div className="d-flex justify-content-end gap-2">
              <button 
                type="button" 
                className="btn btn-secondary" 
                onClick={() => navigate('/admin/users')}
              >
                Cancel
              </button>
              <button 
                type="submit" 
                className="btn btn-primary" 
                disabled={loading || (formData.paymentInfo.cardNumber && !cardValid)}
              >
                {loading ? 'Creating...' : 'Create User'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AdminAddUser;
