import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { FaStore, FaStar, FaUsers, FaPlus } from 'react-icons/fa';

const StoreOwnerDashboard = () => {
  const { user } = useAuth();
  const [stores, setStores] = useState([]);
  const [stats, setStats] = useState({
    totalStores: 0,
    totalRatings: 0,
    averageRating: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOwnerData = async () => {
      try {
        // Fetch store owner's stores
        const storesResponse = await axios.get('/api/stores/owner');
        setStores(storesResponse.data.stores || []);
        
        // Fetch statistics
        const statsResponse = await axios.get('/api/stores/owner/stats');
        setStats(statsResponse.data);
        
        setLoading(false);
      } catch (err) {
        console.error('Error fetching store owner data:', err);
        setError('Failed to load your stores. Please try again later.');
        setLoading(false);
      }
    };

    if (user && user.role === 'store_owner') {
      fetchOwnerData();
    }
  }, [user]);

  if (loading) return <div className="container mt-5 text-center">Loading your dashboard...</div>;
  
  if (error) return (
    <div className="container mt-5">
      <div className="alert alert-danger">{error}</div>
    </div>
  );

  if (!user || user.role !== 'store_owner') {
    return (
      <div className="container mt-5">
        <div className="alert alert-warning">
          This page is only accessible to store owners.
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>My Stores</h2>
        <Link to="/dashboard/stores/create" className="btn btn-primary">
          <FaPlus className="me-2" />
          Create New Store
        </Link>
      </div>
      
      <div className="row mb-4">
        <div className="col-md-4">
          <div className="card text-center h-100">
            <div className="card-body">
              <div className="display-1 text-success mb-3">
                <FaStore />
              </div>
              <h3 className="card-title">Your Stores</h3>
              <p className="card-text display-4">{stats.totalStores}</p>
            </div>
          </div>
        </div>
        
        <div className="col-md-4">
          <div className="card text-center h-100">
            <div className="card-body">
              <div className="display-1 text-warning mb-3">
                <FaStar />
              </div>
              <h3 className="card-title">Average Rating</h3>
              <p className="card-text display-4">
                {stats.averageRating ? stats.averageRating.toFixed(1) : '0.0'}
              </p>
            </div>
          </div>
        </div>
        
        <div className="col-md-4">
          <div className="card text-center h-100">
            <div className="card-body">
              <div className="display-1 text-primary mb-3">
                <FaUsers />
              </div>
              <h3 className="card-title">Total Ratings</h3>
              <p className="card-text display-4">{stats.totalRatings}</p>
            </div>
          </div>
        </div>
      </div>
      
      <div className="card mb-4">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h5 className="mb-0">Your Stores</h5>
          <Link to="/dashboard/stores/create" className="btn btn-success btn-sm">
            <FaPlus className="me-1" /> Add New Store
          </Link>
        </div>
        <div className="card-body">
          {stores.length > 0 ? (
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Status</th>
                    <th>Rating</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {stores.map(store => (
                    <tr key={store.id}>
                      <td>{store.name}</td>
                      <td>{store.address}</td>
                      <td>
                        <span className={`badge bg-${store.status === 'approved' ? 'success' : 'warning'}`}>
                          {store.status}
                        </span>
                      </td>
                      <td>
                        <div className="text-warning">
                          {'★'.repeat(Math.round(store.averageRating || 0))}
                          {'☆'.repeat(5 - Math.round(store.averageRating || 0))}
                        </div>
                        <small>
                          {store.averageRating ? (
                            `${store.averageRating.toFixed(1)} (${store.ratingCount} ratings)`
                          ) : (
                            'No ratings'
                          )}
                        </small>
                      </td>
                      <td>
                        <div className="d-flex gap-2">
                          <Link to={`/stores/${store.id}`} className="btn btn-sm btn-info text-white">
                            View
                          </Link>
                          <Link to={`/stores/${store.id}/edit`} className="btn btn-sm btn-primary">
                            Edit
                          </Link>
                          <Link to={`/stores/${store.id}/ratings`} className="btn btn-sm btn-warning">
                            Ratings
                          </Link>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center p-4">
              <p className="mb-3">You don't have any stores yet.</p>
              <Link to="/dashboard/stores/create" className="btn btn-primary">
                <FaPlus className="me-2" /> Create Your First Store
              </Link>
            </div>
          )}
        </div>
      </div>
      
      <div className="card">
        <div className="card-header">
          <h5 className="mb-0">Recent Ratings</h5>
        </div>
        <div className="card-body">
          {stats.recentRatings && stats.recentRatings.length > 0 ? (
            <div className="list-group">
              {stats.recentRatings.map(rating => (
                <div key={rating.id} className="list-group-item">
                  <div className="d-flex justify-content-between align-items-center">
                    <div>
                      <h6 className="mb-1">{rating.store?.name}</h6>
                      <p className="text-muted mb-1">By {rating.username}</p>
                      <div className="text-warning">
                        {'★'.repeat(rating.rating)}
                        {'☆'.repeat(5 - rating.rating)}
                      </div>
                    </div>
                    <small className="text-muted">
                      {new Date(rating.created_at).toLocaleDateString()}
                    </small>
                  </div>
                  {rating.comment && (
                    <p className="mt-2 mb-0">{rating.comment}</p>
                  )}
                </div>
              ))}
            </div>
          ) : (
            <p className="text-center">No ratings have been submitted for your stores yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default StoreOwnerDashboard;
