import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { FaStar, FaUsers, FaKey } from 'react-icons/fa';

const StoreOwnerDashboard = () => {
  const { user, logout } = useAuth();
  const [storeData, setStoreData] = useState({
    store: null,
    ratings: [],
    averageRating: 0,
    totalRatings: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStoreData = async () => {
      try {
        const response = await axios.get('/api/stores/owner/store');
        setStoreData(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching store data:', err);
        setError('Failed to load your store data');
        setLoading(false);
      }
    };

    if (user?.role === 'store_owner') {
      fetchStoreData();
    }
  }, [user]);

  if (loading) {
    return <div className="container mt-5 text-center">Loading...</div>;
  }

  if (error) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger">{error}</div>
      </div>
    );
  }

  if (!user || user.role !== 'store_owner') {
    return (
      <div className="container mt-5">
        <div className="alert alert-warning">
          This page is only accessible to store owners.
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Store Dashboard</h2>
        <div>
          <Link to="/dashboard/change-password" className="btn btn-warning me-2">
            <FaKey className="me-2" />
            Change Password
          </Link>
          <button onClick={logout} className="btn btn-danger">
            Logout
          </button>
        </div>
      </div>

      {/* Store Stats */}
      <div className="row mb-4">
        <div className="col-md-6">
          <div className="card">
            <div className="card-body text-center">
              <h3>Average Rating</h3>
              <div className="display-4 text-warning">
                <FaStar className="me-2" />
                {storeData.averageRating?.toFixed(1) || '0.0'}
              </div>
              <small className="text-muted">
                Based on {storeData.totalRatings} ratings
              </small>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <div className="card">
            <div className="card-body text-center">
              <h3>Total Reviews</h3>
              <div className="display-4 text-primary">
                <FaUsers className="me-2" />
                {storeData.totalRatings}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Ratings List */}
      <div className="card">
        <div className="card-header">
          <h5 className="mb-0">Customer Ratings</h5>
        </div>
        <div className="card-body">
          {storeData.ratings?.length > 0 ? (
            <div className="table-responsive">
              <table className="table">
                <thead>
                  <tr>
                    <th>User</th>
                    <th>Rating</th>
                    <th>Comment</th>
                    <th>Date</th>
                  </tr>
                </thead>
                <tbody>
                  {storeData.ratings.map(rating => (
                    <tr key={rating.id}>
                      <td>{rating.username}</td>
                      <td>
                        <div className="text-warning">
                          {'★'.repeat(rating.rating)}
                          {'☆'.repeat(5 - rating.rating)}
                        </div>
                      </td>
                      <td>{rating.comment || '-'}</td>
                      <td>
                        {new Date(rating.created_at).toLocaleDateString()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-center my-4">
              No ratings have been submitted yet.
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default StoreOwnerDashboard;
